import React from 'react';

class InputRow extends React.Component {
  constructor(props) {
    super(props);

    this.state = { output: props.prompt, idName: props.prompt };
    idName = idName.toLowerCase();
    for (var i = 0, n = idName.length; i < n; i++) {
      if (!(idName.charAt(i).match(/[a-z]/i))) {
        idName = idName.replace(idName.charAt(i), '');
      }
    }

  }

  render() {
    return (
      <div className="container">
        <label>{props.prompt}</label>
        <input id={idName} type="text" /><br />
      </div>
    );
  }
}

export default InputRow;
