import React from 'react';

class Register extends React.Component {
  state = {
    firstname: '',
    lastname: '',
    username: '',
    password: '',
    confpassword: ''
  };

  onFirstNameChange = (event) => {
    this.setState({ firstname: event.target.value });
  }
  onLastNameChange = (event) => {
    this.setState({ lastname: event.target.value });
  }
  onUsernameChange = (event) => {
    this.setState({ username: event.target.value });
  }
  onPasswordChange = (event) => {
    this.setState({ password: event.target.value });
  }
  onConfPasswordChange = (event) => {
    this.setState({ confpassword: event.target.value });
  }

  onSubmit = (event) => {
    console.log(this.state);
  }

  render() {
    return (
      <div className="container">
        <form>
          <label>First Name: </label>
          <input type="text" onChange={this.onFirstNameChange} /><br />
          <label>Last Name: </label>
          <input type="text" onChange={this.onLastNameChange} /><br />
          <label>Username: </label>
          <input type="text" onChange={this.onUsernameChange} /><br />
          <label>Password: </label>
          <input type="password" onChange={this.onPasswordChange} /><br />
          <label>Confirm Password: </label>
          <input type="password" onChange={this.onConfPasswordChange} /><br />
        </form>
        <div className="login-option">
          <span>Already have an account?</span>
          <span id="login-button" style={{ fontWeight: 'bold', color: 'blue' }}> Login</span>
        </div>

        <button className="register" onClick={this.onSubmit}>Register</button>
      </div>
    );
  }
}

export default Register;
