import React from 'react';
import coingecko from '../api/coingecko';
import '../styles/PriceList.css';

class PriceList extends React.Component {
  state = {
    names: [],
    currPrices: [],
    images: [],
    mktCap: []
  };

  componentDidMount() {
    this.loadAPI();
  }

  loadAPI = async () => {
    const res = await coingecko.get('?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false');
    this.populateState(res);

    console.log(res.data);
  }

  populateState(res) {
    const coins = res.data.map((coin) => {
      return <div>{coin.name}</div>
    });

    const prices = res.data.map((price) => {
      return <div>{price.current_price}</div>
    });

    const imageURLs = res.data.map((image) => {
      return <img className="icon" src={image.image} alt={image.id} />
    });

    const caps = res.data.map((cap) => {
      return <div>{cap.market_cap}</div>
    });

    this.setState({ names: coins });
    this.setState({ currPrices: prices });
    this.setState({ images: imageURLs });
    this.setState({ mktCap: caps })
  }

  render() {
    return (
      <div>
        <h3>Price List</h3>
        <div className="container">
          <div className="icon">{this.state.images[0]}</div>
          <div className="name">{this.state.names[0]}</div>
          <div className="price">{this.state.currPrices[0]}</div>
          <div className="cap">{this.state.mktCap[0]}</div>
        </div>
        <div className="container">
          <div className="icon">{this.state.images[1]}</div>
          <div className="name">{this.state.names[1]}</div>
          <div className="price">{this.state.currPrices[1]}</div>
          <div className="cap">{this.state.mktCap[1]}</div>
        </div>
        <div className="container">
          <div className="icon">{this.state.images[2]}</div>
          <div className="name">{this.state.names[2]}</div>
          <div className="price">{this.state.currPrices[2]}</div>
          <div className="cap">{this.state.mktCap[2]}</div>
        </div>
      </div>
    );
  }
}

export default PriceList;
