import React from 'react';

class Register extends React.Component {
  state = {
    firstname: '',
    lastname: '',
    username: '',
    password: '',
    confpassword: ''
  };

  handleFirstNameChange(event) {

    this.setState({ firstname: "Max" });
    console.log(this.state.firstname);
  }
  handleLastNameChange(event) {
    console.log(event.target.value);
  }
  handleUsernameChange(event) {
    console.log(event.target.value);
  }
  handlePasswordChange(event) {
    console.log(event.target.value);
  }
  handleConfPasswordChange(event) {
    console.log(event.target.value);
  }

  componentDidMount() {
    console.log('Component mounted properly');
  }

  componentDidUpdate() {
    console.log('Component was updated');
  }

  render() {
    return (
      <div className="container">
        <label>First Name: </label>
        <input type="text" onChange={this.handleFirstNameChange} /><br />
        <label>Last Name: </label>
        <input type="text" onChange={this.handleLastNameChange} /><br />
        <label>Username: </label>
        <input type="text" onChange={this.handleUsernameChange} /><br />
        <label>Password: </label>
        <input type="text" onChange={this.handlePasswordChange} /><br />
        <label>Confirm Password: </label>
        <input type="text" onChange={this.handleConfPasswordChange} /><br />
        <div className="login-option">
          <span>Already have an account?</span>
          <span id="login-button" style={{ fontWeight: 'bold', color: 'blue' }}> Login</span>
        </div>

        <button className="register">Register</button>
      </div>
    );
  }
}

export default Register;
