import React from 'react';

class Register extends React.Component {
  state = {
    firstname: '',
    lastname: '',
    username: '',
    password: '',
    confpassword: ''
  };

  onSubmit = (event) => {
    console.log(this.state);
  }

  render() {
    return (
      <div className="container">
        <form>
          <label>First Name: </label>
          <input type="text" onChange={(e) => this.setState({ firstname: e.target.value })} /><br />
          <label>Last Name: </label>
          <input type="text" onChange={(e) => this.setState({ lastname: e.target.value })} /><br />
          <label>Username: </label>
          <input type="text" onChange={(e) => this.setState({ username: e.target.value })} /><br />
          <label>Password: </label>
          <input type="password" onChange={(e) => this.setState({ password: e.target.value })} /><br />
          <label>Confirm Password: </label>
          <input type="password" onChange={(e) => this.setState({ confpassword: e.target.value })} /><br />
        </form>
        <div className="login-option">
          <span>Already have an account?</span>
          <span id="login-button" style={{ fontWeight: 'bold', color: 'blue' }}> Login</span>
        </div>

        <button className="register" onClick={this.onSubmit}>Register</button>
      </div>
    );
  }
}

export default Register;
