import React from 'react';
import Register from './Register';
import PriceList from './PriceList';
import coingecko from '../api/coingecko';

class App extends React.Component {
  state = { prices: [] };

  componentDidMount() {
    this.loadAPI();
  }

  loadAPI = async () => {
    const res = await coingecko.get('?vs_currency=usd&order=market_cap_desc&per_page=10&page=1&sparkline=false');

    this.setState({ prices: res.data });
  }

  onFormSubmit(state) {
    // Send this information to SQL server
    // 'state' contains all register information
  }

  render() {
    return (
      <div className="outline">
        <h3>Crypto Sim</h3>
        <Register onClickSubmit={this.onFormSubmit} />
        <PriceList prices={this.loadAPI} />
      </div>
    );
  }
}

export default App;
